import React, { Component } from 'react';
import { AppBar, Toolbar, Typography, Button, Select, MenuItem, FormControl, Input, withStyles } from '@material-ui/core';

const unidades = [
    "Azul",
    "Avianca",
    "Gol",
    "American Airlines"
];

const SelectStyle = withStyles({
    select: {
        '&:before': {
            borderColor: 'white',
        },
        '&:after': {
            borderColor: 'white',
        },
        color: 'white',
        underline: 'white'
    },
    icon: {
        fill: 'white',
    },
})(Select);

class Header extends Component {

    constructor(props:any) {
        super(props);
        this.state = {};
    }

    handleChange(event: React.ChangeEvent<{ value: unknown }>) {
        console.log(event.target.value as string[]);
    };

    render() {
        return (
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" color="inherit">
                        GRU
                    </Typography>
                    <Button color="inherit">Malha</Button>
                    <FormControl>
                        <SelectStyle id="unit-select" onChange={this.handleChange} input={<Input />} >
                            {unidades.map((unidade) => (
                                <MenuItem key={unidade} value={unidade}>
                                {unidade}
                                </MenuItem>
                            ))}
                        </SelectStyle>
                    </FormControl>
                </Toolbar>
            </AppBar>
        );
    }
}

export default Header;
