import React, { Component } from 'react';
import { AppBar, Toolbar, Typography, Button, Select, MenuItem, FormControl, Input } from '@material-ui/core';

const unidades = [
    "Azul",
    "Avianca",
    "Gol",
    "American Airlines"
];

class Header extends Component {

    constructor(props:any) {
        super(props);
        this.state = { anchorEl : null};
    }

    handleChange(event: React.ChangeEvent<{ value: unknown }>) {
        console.log(event.target.value as string[]);
    };

    render() {
        return (
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" color="inherit">
                        GRU
                    </Typography>
                    <Button color="inherit">Malha</Button>
                    <FormControl>
                        <Select id="unit-select" multiple onChange={this.handleChange} input={<Input />} >
                            {unidades.map((unidade) => (
                                <MenuItem key={unidade} value={unidade}>
                                {unidade}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <Select native id="unidade" value="2">
                        <option value={1}>Gol</option>
                        <option value={2}>Tam</option>
                    </Select>
                </Toolbar>
            </AppBar>
        );
    }
}

export default Header;
