import React from 'react';
import {
  Typography, Divider, Button, TextField
} from '@material-ui/core';

class Edit extends React.Component {

    constructor(props:any) {
        super(props);
        this.state = { inputs: [] };
    }

    handleSubmit(){
        alert('hi!');
    }

    render() {
        return (
            <React.Fragment>
                <Typography variant="h4">Mash</Typography>

                <Divider/>

                <form className="class" autoComplete="off" onSubmit={this.handleSubmit}>
                    <div>
                        <TextField required id="rpe" label="Resposável RPE" />
                    </div>
                    <div>
                        <TextField required id="email1" label="E-mail Responsável 1" inputProps={{ pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i }}/>
                    </div>
                    <div>
                        <TextField id="email2" label="E-mail Responsável 2" inputProps={{ pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i }}/>
                    </div>
                    <div>
                        <TextField required id="phone1" label="Telefone 1" inputProps={{ pattern: "(\(?\d{2}\)?\s)?(\d{4,5}\-\d{4})" }}/>
                    </div>
                    <div>
                        <TextField id="phone2" label="Telefone 2" inputProps={{ pattern: "(\(?\d{2}\)?\s)?(\d{4,5}\-\d{4})" }}/>
                    </div>
                    <div>
                        <TextField required id="airline" label="Nome CIA Aérea" />
                    </div>
                    <div>
                        <TextField required id="initials" label="Sigla ICIA" />
                    </div>
                    <div>
                        <Button type="submit">Salvar</Button>
                    </div>
                </form>

                <Divider/>

            </React.Fragment>
        );
    }
}

export default Edit;
