}

interface MyState {
    id: string
}

const mockup = [
    {
        id: 1,
        rpe: "RPE1",
        email1: "Email1-1",
        email2: "Email2-1",
        telefone1: "Telefone1-1",
        telefone2: "Telefone2-1",
        cia: "CIA1",
        sigla: "Sigla1",
    },
    {
        id: 2,
        rpe: "RPE2",
        email1: "Email1-2",
        email2: "Email2-2",
        telefone1: "Telefone1-2",
        telefone2: "Telefone2-2",
        cia: "CIA2",
        sigla: "Sigla2",
    },
    {
        id: 3,
        rpe: "RPE3",
        email1: "Email1-3",
        email2: "Email2-3",
        telefone1: "Telefone1-3",
        telefone2: "Telefone2-3",
        cia: "CIA3",
        sigla: "Sigla3",
    }
]

class Edit extends Component<MyProps, MyState> {

    constructor(props:any) {
        super(props);
        this.find = this.find.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        let params = props.match.params;
        this.state = { 
            user : {
                id: params.id || null
            }
        };
    }

