    }

    handleSubmit(){
        console.log(this.state.id);
    }

    render() {
        return (
            <React.Fragment>
                <Typography variant="h4"></Typography>

                <Divider/>

                <form className="class" autoComplete="off" onSubmit={this.handleSubmit}>
                    <div>
                        <TextField required id="rpe" label="Resposável RPE" />
                    </div>
                    <div>
                        <TextField required id="email1" label="E-mail Responsável 1"/>
                    </div>
                    <div>
                        <TextField id="email2" label="E-mail Responsável 2"/>
                    </div>
                    <div>
                        <TextField required id="phone1" label="Telefone 1"/>
                    </div>
                    <div>
                        <TextField id="phone2" label="Telefone 2"/>
                    </div>
                    <div>
                        <TextField required id="airline" label="Nome CIA Aérea" />
                    </div>
                    <div>
                        <TextField required id="initials" label="Sigla ICIA" />
                    </div>

                    <Divider/>

                    <div>
                        <Button type="submit">Salvar</Button>
                    </div>
                </form>

            </React.Fragment>
        );
    }
}

export default Edit;
