import React, { Component } from 'react';
import {
  Typography, Divider, Button, TextField
} from '@material-ui/core';
import { RouteComponentProps } from 'react-router';

interface IUser {
    rpe: string,
    email1: string,
    email2: string,
    telefone1: string,
    telefone2: string,
    cia: string,
    sigla: string
}

interface MyState {
    id: string,
    [key: string]: any
}

class Edit extends Component<RouteComponentProps, MyState> {

    constructor(props:any) {
        super(props);
        this.find = this.find.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        let params = props.match.params;
        this.state = { 
            id: params.id || null,
            rpe: '',
            email1: '',
            email2: '',
            telefone1: '',
            telefone2: '',
            cia: '',
            sigla: ''
        };
    }

    find(){

    }

    handleSubmit(e:React.FormEvent<HTMLFormElement>): void{
        e.preventDefault();
        console.log(this.state);
    }

    private handleInputChanges = (e:React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        console.log(e.currentTarget.id);
        console.log(this.state.rpe);
        /*this.setState({
            [e.currentTarget.id]: e.currentTarget.value,
        })*/
    }

    render() {
        return (
            <React.Fragment>
                <Typography variant="h4">Usuário</Typography>

                <Divider/>

                <form className="class" autoComplete="off" onSubmit={this.handleSubmit}>
                    <div>
                        <TextField required id="rpe" label="Resposável RPE" value={this.state.rpe} onChange={(e) => { this.handleInputChanges(e) }}/>
                    </div>
                    <div>
                        <TextField required id="email1" label="E-mail Responsável 1" value={this.state.email1}/>
                    </div>
                    <div>
                        <TextField id="email2" label="E-mail Responsável 2" value={this.state.email2}/>
                    </div>
                    <div>
                        <TextField required id="telefone1" label="Telefone 1" value={this.state.telefone1}/>
                    </div>
                    <div>
                        <TextField id="telefone2" label="Telefone 2" value={this.state.telefone2}/>
                    </div>
                    <div>
                        <TextField required id="cia" label="Nome CIA Aérea" value={this.state.cia}/>
                    </div>
                    <div>
                        <TextField required id="sigla" label="Sigla ICIA" value={this.state.sigla}/>
                    </div>

                    <Divider/>

                    <div>
                        <Button type="submit">Enviar</Button>
                    </div>
                </form>

            </React.Fragment>
        );
    }
}

export default Edit;
