import React, { Fragment } from 'react';
import Drawer from './components/template/Drawer';
import Home from './pages/Home';
import Mash from './pages/Mash';
import { UserList, UserForm } from './pages/user/Index';
import { BrowserRouter as Router, Switch, Route, withRouter} from 'react-router-dom';
import { CssBaseline, Container } from '@material-ui/core';

const App = () => {
  return (
    <Fragment>
      <CssBaseline/>
      <Router>
      <Drawer>
        <Container>

            <Switch>
              <Route path="/" exact component={withRouter(Home)}/>
              <Route path="/malhas" exact component={(Mash)}/>
              <Route path="/usuarios" exact component={withRouter(UserList)}/>
              <Route path="/usuario/:id?" component={withRouter(UserForm)}/>
            </Switch>

        </Container>
      </Drawer>
      </Router>
    </Fragment>
  );
}

export default App;
