import React from 'react';
import {
  Typography, Divider, Button, TextField
} from '@material-ui/core';

class Mash extends React.Component {

    constructor(props:any) {
        super(props);
        this.state = { inputs: [] };
    }

    handleSubmit(){
        alert('hi!');
    }

    render() {
        return (
            <React.Fragment>
                <Typography variant="h4">Mash</Typography>

                <Divider/>

                <form className="class" autoComplete="off" onSubmit={this.handleSubmit}>
                    <div>
                        <TextField required id="rpe" label="Resposável RPE" />
                    </div>
                    <div>
                        <TextField required id="email1" label="E-mail Responsável 1" inputProps={{ pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i }}/>
                    </div>
                    <div>
                        <TextField required id="email1" label="E-mail Responsável 2" />
                    </div>
                    <div>
                        <TextField required id="min" label="Min" />
                    </div>
                    <div>
                        <TextField required id="max-length" label="Max-Length" />
                    </div>
                    <Button type="submit">Butão</Button>
                </form>

                <Divider/>

            </React.Fragment>
        );
    }
}

export default Mash;
