import React from 'react';
import {
  Typography, Divider, Button, TextField 
} from '@material-ui/core';
import StickyHeadTable from '../../components/template/StickyTable';
import UserService from '../../service/UserService';
import { RouteComponentProps } from 'react-router';

interface CompanyColumn {
    id: 'cia' | 'sigla' | 'cnpj' | 'pais' | 'gerente' | 'telefone-gerente1' | 'telefone-gerente2' | 'email-gerente1' | 'email-gerente2' | 'responsavel1' | 'responsavel2' | 'responsavel3' | 'responsavel4' | 'responsavel5' | 'responsavel6' | 'responsavel7' | 'responsavel8' | 'responsavel9' | 'responsavel10' | 'edit';
    label: string;
    minWidth?: number;
    align?: 'right';
    action?: boolean;
    url?: string;
    format?: (value: number) => string;
}

const columns: CompanyColumn[] = [
    { id: 'cia', label: 'Nome CIA', minWidth: 170 },
    { id: 'sigla', label: 'Sigla ICAO', minWidth: 100 },
    { id: 'cnpj', label: 'CNPJ', minWidth: 100 },
    { id: 'pais', label: 'País de Origem', minWidth: 100 },
    { id: 'telefone-gerente1', label: 'Telefone Gerente 1', minWidth: 100 },
    { id: 'telefone-gerente2', label: 'Telefone Gerente 2', minWidth: 100 },
    { id: 'email-gerente1', label: 'E-mail 1', minWidth: 100 },
    { id: 'email-gerente2', label: 'E-mail 2', minWidth: 100 },
    { id: 'edit', label: 'Ação', minWidth: 100, action: true, url: '/companhia/'}
];

interface MyState {
    users: any[]
}

class List extends React.Component<RouteComponentProps, MyState> {

    constructor(props:any) {
        super(props);
        let users = UserService.getAll();
        this.state = { users : users };
    }

    handleSubmit(){
    }

    render() {
        return (
            <React.Fragment>
                <Typography variant="h4">Usuários</Typography>

                <Divider/>

                <form className="class" autoComplete="off" onSubmit={this.handleSubmit}>

                    {columns.map((field:any) => {
                        return (
                            <div>
                                <TextField id={field.id} label={field.label} />
                            </div>
                        )
                    })}

                </form>

                <StickyHeadTable columns={columns} rows={this.state.users}/>

            </React.Fragment>
        );
    }
}

export default List;
