import React, { Component } from 'react';
import { AppBar, Toolbar, Typography, Button, Select, MenuItem, FormControl, Input, makeStyles, Theme, createStyles } from '@material-ui/core';

const unidades = [
    "Azul",
    "Avianca",
    "Gol",
    "American Airlines"
];

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    button: {
      display: 'block',
      marginTop: theme.spacing(2),
    },
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
  }),
);

class Header extends Component {

    constructor(props:any) {
        super(props);
        this.state = { classes: useStyles()};
    }

    handleChange(event: React.ChangeEvent<{ value: unknown }>) {
        console.log(event.target.value as string[]);
    };

    render() {
        return (
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" color="inherit">
                        GRU
                    </Typography>
                    <Button color="inherit">Malha</Button>
                    <FormControl>
                        <Select id="unit-select" onChange={this.handleChange} input={<Input />} >
                            {unidades.map((unidade) => (
                                <MenuItem key={unidade} value={unidade}>
                                {unidade}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </Toolbar>
            </AppBar>
        );
    }
}

export default Header;
