import React, { Component } from 'react';
import {
  Typography, Divider, Button, TextField
} from '@material-ui/core';
import { RouteComponentProps } from 'react-router';

interface IUser {
    rpe: string,
    email1: string,
    email2: string,
    phone1: string,
    phone2: string,
    airline: string,
    initials: string
}

interface MyState {
    id: string,
    [key: string]: any,
    values: IUser[]
}

class Edit extends Component<RouteComponentProps, MyState> {

    constructor(props:any) {
        super(props);
        this.find = this.find.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        let params = props.match.params;
        this.state = { 
            id: params.id || null,
            values: []
        };
    }

    find(){

    }

    handleSubmit(e:React.FormEvent<HTMLFormElement>): void{
        e.preventDefault();
        console.log(this.state);
    }

    private handleInputChanges = (e:React.FormEvent<HTMLInputElement>) => {
        this.setState({
            [e.currentTarget.id]: e.currentTarget.value,
        })
    }

    render() {
        return (
            <React.Fragment>
                <Typography variant="h4">Usuário</Typography>

                <Divider/>

                <form className="class" autoComplete="off" onSubmit={this.handleSubmit}>
                    <div>
                        <TextField required id="rpe" label="Resposável RPE" value={this.state.rpe}/>
                    </div>
                    <div>
                        <TextField required id="email1" label="E-mail Responsável 1" value={this.state.email1}/>
                    </div>
                    <div>
                        <TextField id="email2" label="E-mail Responsável 2" value={this.state.email2}/>
                    </div>
                    <div>
                        <TextField required id="phone1" label="Telefone 1" value={this.state.phone1}/>
                    </div>
                    <div>
                        <TextField id="phone2" label="Telefone 2" value={this.state.phone2}/>
                    </div>
                    <div>
                        <TextField required id="airline" label="Nome CIA Aérea" value={this.state.airline}/>
                    </div>
                    <div>
                        <TextField required id="initials" label="Sigla ICIA" value={this.state.initials}/>
                    </div>

                    <Divider/>

                    <div>
                        <Button type="submit">Enviar</Button>
                    </div>
                </form>

            </React.Fragment>
        );
    }
}

export default Edit;
